$(document).ready(function(){calc();});function calc(){var flask=document.getElementById("flask");var tobacco=document.getElementById("tobacco");var count=document.getElementById("count");var result=document.getElementById("result");var resultInput=document.getElementById("result-input");var priceOne=document.getElementById("price-hookah-one").value;var priceDay=document.getElementById("price-hookah-day").value;var datefrom=document.getElementById("datefromhiden");datefrom=datefrom==null?0:datefrom.value;var dateto=document.getElementById("datetohiden");dateto=dateto==null?0:dateto.value;var countDay=(dateto-datefrom)/60/60/24;countDay=countDay|0;countDay=(countDay<0?0:countDay);var price=0;for(var i=0;i<flask.selectedOptions.length;i++){price+=parseInt(flask.selectedOptions[i].value.split(':')[1]);}for(var i=0;i<tobacco.selectedOptions.length;i++){price+=parseInt(tobacco.selectedOptions[i].value.split(':')[1]);}price+=parseInt(count.value)*priceOne;price+=parseInt(count.value)*countDay*priceDay;price=price|0;result.innerHTML=price;resultInput.value=price;}